mapper.select:
SELECT 

FROM pro_order a

WHERE a.id = #{id}

SELECT 

FROM pro_order a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.code = #{code}


		AND a.receive_name = #{receiveName}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM pro_order a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.code = #{code}


		AND a.receive_name = #{receiveName}


		AND a.status = #{status}

	AND a.status != 3




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM pro_order a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE pro_order SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
